import React, { useEffect, useState } from 'react';
import { Messages } from './Messages';
import { Chats } from './Chats'; 
import { Panes, Pane } from './Panes';

export function App() {
    let [chats, setChats] = useState([]);
    let [selectedId, setSelectedId] = useState(1);
    let [messages, setMessages] = useState([]);
    let [lastPoll, setLastPoll] = useState(Date.now());
  
useEffect(() => {
    import('./data/chats.js')
        .then(module => {
            let chats = module.chats;
            setChats(module.posts);
            setSelectedId(chats[0].id);
        });
    }, []);

    // useEffect(() => {
    //   fetch('https://jsonplaceholder.typicode.com/posts')
    //     .then(res => res.json())
    //     .then((posts) => setPosts(posts))
    // }, []);
  
    useEffect(() => {
      if (!selectedId) {
        return;
      }
      import(`./data/messages${selectedId}.js`)
        .then(module => {
            let messages = module.messages;
            setMessages(messages);
        })
      }, [selectedId], lastPoll);
//       fetch(`https://jsonplaceholder.typicode.com/comments?postId=${selectedId}`)
//       .then(res => res.json())
//       .then((comments) => setComments(comments))
//   }, [selectedId]);

useEffect(() => {
    setTimeout(() => {
        setLastPoll(Date.now());
    }, 1000);
}, [lastPoll]);
    
  let selectedChat = chats.find((p) => p.id === selectedId);
  
    return <Panes>
        <Pane width={'35%'} minWidth={'300px'}
         header={'Chats'}
         body={<Chats chats={chats} onSelectChat={setSelectedId}></Chats>}>
      </Pane>
      <Pane width={'65%'}
        header={`${selectedChat?.title} (${selectedChat?.id})`}
        body={<Messages messages={messages}></Messages>}>
      </Pane>
    </Panes>;
  }
