import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import {App} from './App';


function Comments(props) {
  if (!props.post || !props.comments) {
    return '';
  }
  let commentStyle = {
    border: '2px solid',
    padding: '0.5em'
  };
  let titletStyle = {
    padding: '0.5em',
    color: 'blue',
    textAlign: 'center'
  };
  return <div>
    <h2 style={titletStyle}>{props.post.title} ({props.post.id})</h2> 
    <ul>
      {props.comments.map(comment => <div key={comment.id} style={commentStyle}>
        <p>postId: {comment.postId}</p>
        <p>name: {comment.name}</p>
        <p>email: {comment.email}</p>
        <p>{comment.body}</p>
      </div>)}
    </ul>
  </div>
}

let Post = styled.div(props => ({
    border: '2px solid',
    padding: '0.5em',
    cursor: "pointer"
}));

function Posts(props) {
  return <ul>
    {props.posts.map(post => {
      return <Post style={postStyle} key={post.id} onClick={() => props.onSelectPost(post.id)}>
      {post.title} ({post.id})
      </Post>
    })}
  </ul>
}

let Panes = styled.div({
  display: 'flex',
  width: '100vw',
  height: '100vh'
});

let Pane = styled.div(props => ({
  overFlow: 'auto',
  ...props
}));


ReactDOM.render(
  <App />,
  document.getElementById('root')
);
