import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

let Post = styled.div({
    border: '2px solid',
    padding: '0.5em',
    cursor: "pointer"
});

export function Posts(props) {
  return <ul>
    {props.posts.map(post => {
      return <Post style={postStyle} key={post.id} onClick={() => props.onSelectPost(post.id)}>
      {post.title} ({post.id})
      </Post>
    })}
  </ul>
}
